import React from 'react';
import { AppContext } from '../context/AppContext';
import '../styles/App.css';
import LoginContainer from './LoginContainer';
import UserInfo from './UserInfo';
import DataService from '../context/DataService';

class App extends React.Component {
  static contextType = AppContext;
  constructor(props) {
    super(props);
    this.dataProvider = new DataService(this);
    this.state = {
      user: null,
    };
  }

  async componentDidMount() {
    const user = await this.context.getUser();
    this.setState({ ...this.state, user });
  }
  render() {
    return (
      <AppContext.Provider value={this.dataProvider}>
        {this.state.user ? <UserInfo /> : <LoginContainer />}
      </AppContext.Provider>
    );
  }
}

export default App;
