export default class DataService {
  constructor(app) {
    this.app = app;
    this.app.state = {
      user: null,
    };
  }
  get data() {
    return this.app.this.state;
  }
  set data(newState) {
    this.app.setState(newState);
  }
  async fetchUser() {
    const response = await fetch('/api/user');
    const user = await response.json();
    this.app.setState()
  }
}
