import React from 'react';
import '../styles/App.css';
import Logo from './Logo';
import SignUpPageTitle from './SignUpPageTitle';
import GoBackButton from './GoBackButton';
import { Link } from 'react-router-dom';

export default function SignUpContainer() {
  const [state, setState] = React.useState({
    email: '',
    password: '',
    terms: false,
  });
  const updateState = (e) => {
    console.log('VALUE', e.target.value);
    console.log('NAME', e.target.name);
    setState({ ...state, [e.target.name]: e.target.value });
  };
  const sendDataToServer = async (e) => {
    e.preventDefault();
    await fetch('http://localhost:5000/api/auth/login', {
      method: 'POST',
      headers: {
        'content-type': 'application/json',
      },
      body: JSON.stringify(state),
    });
  };
  const handleCheckboxClick = (e) => {
    console.log('VALUE', e.target.value);
    console.log('NAME', e.target.name);
    setState({ ...state, [e.target.name]: e.target.checked });
  };
  console.log(state);
  return (
    <div className='App-Container'>
      <Logo />
      <SignUpPageTitle />
      <form onSubmit={sendDataToServer}>
        <input
          className='Email form-control mb-4'
          placeholder='Email:'
          type='text'
          id='todo-item'
          name='email'
          onChange={updateState}
        />
        <input
          className='Password form-control mb-4'
          placeholder='Password:'
          type='text'
          name='password'
          id='todo-item'
          onChange={updateState}
        />
        <input
          id='checkbox'
          type='checkbox'
          name='terms'
          defaultChecked={state.terms}
          onClick={(e) => handleCheckboxClick(e)}
        />
        <label className='Agree'>&nbsp; &nbsp;I agree to the</label>
        <Link to='/termsandconditions'>&nbsp;terms and conditions</Link>
        <button type='submit' className='LoginButton btn btn-light mb-4'>
          Sign up
        </button>
        {/* <Email />
      <Password />
      <Password />
      <CheckboxTerms />
      <SignUpButton /> */}
        <GoBackButton />
      </form>
    </div>
  );
}
