export default class DataService {
  constructor(app) {
    this.app = app;
    this.app.state = { user: null };
    this.setUp();
  }
  get data() {
    return this.app.state;
  }
  set data(newState) {
    this.app.setState(newState);
  }
  // SETTING UP CONTEXT
  async setUp() {
    const user = await this.fetchUser();
    if (!user) {
      return;
    }
    const location = await this.getLocation();
    if (location !== user.location) {
      await this.updateLocation(location);
    }
    this.app.setState({ ...this.app.state, user });
  }
  // FETCHIN LOGGED IN USER DATA
  async fetchUser() {
    try {
      const response = await fetch('/api/user');
      const user = await response.json();
      return user;
    } catch (error) {
      console.log('not ok');
    }
  }
  // CREATING USER
  async createUser(user) {
    try {
      await fetch('/api/user', {
        method: 'POST',
        headers: {
          'content-type': 'application/json',
        },
        body: JSON.stringify(user),
      });
      console.log('User successfuly created');
    } catch (error) {
      console.error(error);
    }
  }
  // FETCHING ORHER USERS BASED ON FILTER
  async getUsers(filter) {
    try {
      await fetch('/api/user/all', {
        method: 'POST',
        headers: {
          'content-type': 'application/json',
        },
        body: JSON.stringify(filter),
      });
      console.log('Fetching users based on the filter');
    } catch (error) {
      console.error(error);
    }
  }
  // LOGOUT
  async logout() {
    try {
      await fetch('/api/auth/logout');
      window.location.reload();
    } catch (error) {
      console.error(error);
    }
  }
  // GET CURRENT LOCATION OF USER
  async getLocation() {
    return new Promise((res, rej) => {
      navigator.geolocation.getCurrentPosition((position) => {
        res(position);
      });
    });
  }
  // UPDATE LOCATION
  async updateLocation(location) {
    try {
      console.log('This should be location', location, JSON.stringify(location.coords));
      await fetch('/api/user', {
        method: 'PATCH',
        headers: {
          'content-type': 'application/json',
        },
        body: location,
      });
    } catch (error) {
      console.error(error);
    }
  }
}
